mapper.select:

	SELECT * 
	FROM ( 
        SELECT /*+INDEX_DESC(TBL_BOARD TBL_BOARD_PK) */ ROWNUM RN, BNO, TITLE, CONTENT, WRITER, regdate, updatedate
        FROM   TBL_BOARD
        where

           ROWNUM <= #{pageNum} * #{amount} 
        )        
	WHERE RN > (#{pageNum} - 1) * #{amount}

mapper.insert:

	SELECT SEQ_BOARD.NEXTVAL FROM DUAL

INSERT INTO TBL_BOARD(
	BNO
   ,TITLE
   ,CONTENT
   ,WRITER
) VALUES (
	#{bno}
   ,#{title}
   ,#{content}
   ,#{writer}
)
